#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 60;
const int MAXK = 60;

int main() {
    int N, K;
    cin >> N >> K;

    vector<int> a(N);
    for (int i = 0; i < N; i++) {
        cin >> a[i];
    }

    vector<int> r(K);
    for (int i = 0; i < K; i++) {
        cin >> r[i];
    }

    vector<int> b(K);
    for (int i = 0; i < K; i++) {
        cin >> b[i];
    }

    vector<int> dp(N + 1, 1e9);
    dp[0] = 0;

    for (int i = 0; i < N; i++) {
        for (int j = 0; j <= i; j++) {
            int cost = 0;
            for (int k = 0; k <= K; k++) {
                int minR = min(r[k], a[i] - j);
                int minB = min(b[k], a[i] - j);
                cost += abs(minR - r[k]) + abs(minB - b[k]);
            }
            dp[i + 1] = min(dp[i + 1], dp[j] + cost);
        }
    }

    if (dp[N] == 1e9) {
        cout << -1 << endl;
    } else {
        cout << dp[N] << endl;
    }

    return 0;
}
