import java.io.*;
import java.util.*;

public class Main {
    static final int N = 60;
    static final int K = 60;
    static final int M = 50;

    static int[][] dp = new int[N + 1][K * 2 + 1];
    static int[][] a = new int[N + 1][K + 1];
    static int[] r = new int[K + 1];
    static int[] b = new int[K + 1];

    static int solve(int n, int m, int red, int black) {
        if (n == 0) return 0;
        if (dp[n][red + black] != -1) return dp[n][red + black];
        int ans = Integer.MAX_VALUE;
        for (int i = 0; i <= Math.min(m, n); i++) {
            int cost = Math.max(0, a[n][i] - Math.min(r[i], black));
            int res = solve(n - 1, m, red - r[i], black - a[n][i]) + cost;
            ans = Math.min(ans, res);
        }
        dp[n][red + black] = ans;
        return ans;
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        for (int i = 1; i <= n; i++) {
            a[i][0] = sc.nextInt();
        }
        for (int i = 1; i <= m; i++) {
            r[i] = sc.nextInt();
        }
        for (int i = 1; i <= m; i++) {
            b[i] = sc.nextInt();
        }
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= m; j++) {
                a[i][j] = a[i][j - 1] + r[j];
            }
        }
        Arrays.fill(dp[0], -1);
        Arrays.fill(dp[0], 0);
        for (int i = 1; i <= m; i++) {
            dp[0][i] = 0;
        }
        int ans = solve(n, m, m, m);
        if (ans == Integer.MAX_VALUE) {
            System.out.println(-1);
        } else {
            System.out.println(ans);
        }
    }
}
