#include <stdio.h>
#include <stdlib.h>

#define MOD 998244353

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n;
    scanf("%d", &n);
    int p[n + 1];
    for (int i = 1; i <= n; i++) {
        scanf("%d", &p[i]);
    }

    long long ans = 1;
    for (int i = 1; i <= n; i++) {
        int cnt = 0;
        for (int j = i + 1; j <= n; j++) {
            if (p[j] < p[i]) cnt++;
        }
        ans = ans * (cnt + 1) % MOD;
    }

    for (int i = 1; i <= n; i++) {
        int cnt = 0;
        for (int j = i + 1; j <= n; j++) {
            if (p[j] > p[i]) cnt++;
        }
        ans = ans * (cnt + 1) % MOD;
    }

    printf("%lld\n", ans);
    return 0;
}
