#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int N = 2e5 + 5;
const int mod = 998244353;

int n, a[N], b[N], c[N], d[N], fa[N], siz[N], inv[N], f[N], g[N];
vector<int> e[N];

void dfs(int u, int fa) {
    for (int v : e[u]) {
        if (v == fa) continue;
        dfs(v, u);
        f[u] = (1LL * f[u] * (siz[v] + 1) % mod + 1LL * f[v] * (siz[u] + 1) % mod) % mod;
        g[u] = (1LL * g[u] * (siz[v] + 1) % mod + 1LL * g[v] * (siz[u] + 1) % mod) % mod;
    }
    f[u] = 1;
    g[u] = 1;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin >> n;
    for (int i = 1; i < n; i++) {
        cin >> a[i] >> b[i];
        e[a[i]].push_back(b[i]);
        e[b[i]].push_back(a[i]);
    }
    for (int i = 1; i <= n; i++) {
        cin >> c[i] >> d[i];
        e[c[i]].push_back(d[i]);
        e[d[i]].push_back(c[i]);
    }
    for (int i = 1; i <= n; i++) {
        inv[i] = qpow(i, mod - 2);
    }
    dfs(1, 0);
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = (ans + 1LL * f[i] * g[i] % mod * inv[siz[i] + 1] % mod) % mod;
    }
    cout << ans << '\n';
    return 0;
}

int qpow(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) res = 1LL * res * a % mod;
        a = 1LL * a * a % mod;
        b >>= 1;
    }
    return res;
}
