import java.io.*;
import java.util.*;

public class Main {
    static final int N = 200005;
    static final int mod = 998244353;
    static int n, ans;
    static int[] fa = new int[N], siz = new int[N];
    static int[] a = new int[N], b = new int[N], c = new int[N], d = new int[N];
    static int[] f = new int[N], g = new int[N];
    static int[] inv = new int[N];

    static int find(int x) {
        return fa[x] == x ? x : (fa[x] = find(fa[x]));
    }

    static void merge(int x, int y) {
        x = find(x);
        y = find(y);
        if (x == y) return;
        if (siz[x] < siz[y]) {
            int temp = x;
            x = y;
            y = temp;
        }
        fa[y] = x;
        siz[x] += siz[y];
        f[x] = (int) ((long) f[x] * (f[y] + 1) % mod);
        g[x] = (int) ((long) g[x] * (g[y] + 1) % mod);
    }

    static void solve(int x, int y) {
        if (x == y) {
            f[x] = g[x] = 1;
            return;
        }
        solve(x, find(y));
        merge(x, y);
        f[x] = (int) ((long) f[x] * (f[y] + 1) % mod);
        g[x] = (int) ((long) g[x] * (g[y] + 1) % mod);
    }

    static void init() {
        inv[1] = 1;
        for (int i = 2; i <= n; i++) inv[i] = (int) ((long) (mod - mod / i) * inv[mod % i] % mod);
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        n = Integer.parseInt(br.readLine());
        for (int i = 1; i <= n; i++) {
            fa[i] = i;
            siz[i] = 1;
            f[i] = g[i] = 1;
        }
        for (int i = 1; i <= n; i++) {
            String[] s = br.readLine().split(" ");
            a[i] = Integer.parseInt(s[0]);
            b[i] = Integer.parseInt(s[1]);
            merge(a[i], b[i]);
        }
        for (int i = 1; i <= n; i++) {
            String[] s = br.readLine().split(" ");
            c[i] = Integer.parseInt(s[0]);
            d[i] = Integer.parseInt(s[1]);
            merge(c[i], d[i]);
        }
        for (int i = 1; i <= n; i++) {
            solve(a[i], b[i]);
            solve(c[i], d[i]);
        }
        init();
        for (int i = 1; i <= n; i++) {
            ans = (int) ((ans + (long) (f[find(i)] + 1) * (g[find(i)] + 1) % mod * inv[2] % mod) % mod);
        }
        System.out.println(ans);
    }
}
