import sys
input = sys.stdin.readline

N = 2 * 10 ** 5 + 5
mod = 998244353

def qpow(a, b):
    ans = 1
    while b:
        if b & 1:
            ans = ans * a % mod
        a = a * a % mod
        b >>= 1
    return ans

def inv(x):
    return qpow(x, mod - 2)

def add(x, y):
    x += y
    if x >= mod:
        x -= mod
    return x

def sub(x, y):
    x -= y
    if x < 0:
        x += mod
    return x

def mul(x, y):
    return x * y % mod

def C(n, m):
    if n < m:
        return 0
    return fac[n] * inv(fac[m]) % mod * inv(fac[n - m]) % mod

fac = [1] * N
for i in range(1, N):
    fac[i] = fac[i - 1] * i % mod

def solve():
    n = int(input())
    a = [[] for _ in range(n + 1)]
    b = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        u, v = map(int, input().split())
        a[u].append(v)
        a[v].append(u)
    for _ in range(n - 1):
        u, v = map(int, input().split())
        b[u].append(v)
        b[v].append(u)

    f = [0] * (n + 1)
    g = [0] * (n + 1)
    f[1] = g[1] = 1
    for i in range(2, n + 1):
        f[i] = (f[i - 1] * (n - i + 1) + g[i - 1] * i) % mod
        g[i] = (f[i - 1] * i + g[i - 1] * (n - i + 1)) % mod

    ans = 0
    for i in range(1, n + 1):
        for j in a[i]:
            if j != 1:
                a[i].remove(j)
        for j in b[i]:
            if j != 1:
                b[i].remove(j)
        if a[i]:
            ans = add(ans, mul(C(n - 1, i - 1), f[i]))
        if b[i]:
            ans = add(ans, mul(C(n - 1, i - 1), g[i]))

    ans = mul(ans, inv(2 ** (n - 1)))
    print(ans)

solve()
