#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdbool.h>

#define N 100005
#define K 200005

typedef struct {
    int to, next;
} edge;

typedef struct {
    int c, d;
} color;

int n, k;
int head[N], tot;
edge e[N << 1];
color col[N];
int colcnt[N];
int colsum[N];
int colcntsum[N];
int colsumsum[N];
int ans[N];

void add_edge(int u, int v) {
    e[tot].to = v;
    e[tot].next = head[u];
    head[u] = tot++;
}

void dfs(int u, int fa) {
    for (int i = head[u]; i != -1; i = e[i].next) {
        int v = e[i].to;
        if (v == fa) continue;
        dfs(v, u);
        colcntsum[u] += colcnt[v];
        colsumsum[u] += colsum[v];
    }
    int cnt = 0;
    for (int i = head[u]; i != -1; i = e[i].next) {
        int v = e[i].to;
        if (v == fa) continue;
        cnt++;
        colcnt[u] = std::max(colcnt[u], colcnt[v] + 1);
        colsum[u] = std::max(colsum[u], colsum[v] + 1);
        colcnt[u] = std::max(colcnt[u], colcntsum[u] - colcnt[v]);
        colsum[u] = std::max(colsum[u], colsumsum[u] - colsum[v]);
    }
    colcnt[u]++;
    colsum[u]++;
    colcnt[u] = std::max(colcnt[u], colcntsum[u]);
    colsum[u] = std::max(colsum[u], colsumsum[u]);
    ans[u] = std::max(colcnt[u], colsum[u]);
}

int main() {
    scanf("%d%d", &n, &k);
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        add_edge(u, v);
        add_edge(v, u);
    }
    for (int i = 1; i <= n; i++) head[i] = -1;
    for (int i = 1; i <= n; i++) {
        scanf("%d%d", &col[i].c, &col[i].d);
        colcnt[i] = 1;
        colsum[i] = 1;
        colcntsum[i] = 1;
        colsumsum[i] = 1;
        if (col[i].c == col[i].d) {
            colcnt[i]++;
            colsum[i]++;
        }
    }
    dfs(1, 0);
    for (int i = 1; i <= n; i++) printf("%d\n", ans[i]);
    return 0;
}
