#include <iostream>
#include <vector>
#include <set>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;
const int K = 2e5 + 5;

int n, k;
vector<int> g[N];
int c[N], d[N];
int color[N];
int colorCount[N];
int colorSet[N];
int colorSetCount[N];
int dp[N];

void dfs(int u, int p) {
    set<int> s;
    for (int v : g[u]) {
        if (v != p) {
            dfs(v, u);
            for (int x : colorSet[v]) {
                s.insert(x);
            }
        }
    }
    color[u] = c[u];
    colorCount[u] = 1;
    if (d[u] != c[u]) {
        color[u] = d[u];
        colorCount[u] = 1;
    }
    if (c[u] != d[u]) {
        s.insert(c[u]);
        s.insert(d[u]);
    }
    for (int v : g[u]) {
        if (v != p) {
            for (int x : colorSet[v]) {
                s.insert(x);
            }
            colorSetCount[u] = max(colorSetCount[u], colorSetCount[v]);
            for (int x : colorSet[v]) {
                if (s.find(x) == s.end()) {
                    colorSetCount[u]++;
                }
            }
        }
    }
    colorSet[u].insert(color[u]);
    colorSetCount[u]++;
    for (int v : g[u]) {
        if (v != p) {
            for (int x : colorSet[v]) {
                if (s.find(x) == s.end()) {
                    colorSetCount[u]++;
                }
            }
        }
    }
    dp[u] = colorSetCount[u];
    for (int v : g[u]) {
        if (v != p) {
            dp[u] = max(dp[u], dp[v] + colorSetCount[u]);
        }
    }
    for (int v : g[u]) {
        if (v != p) {
            for (int x : colorSet[v]) {
                s.insert(x);
            }
            colorSetCount[u] = max(colorSetCount[u], colorSetCount[v]);
            for (int x : colorSet[v]) {
                if (s.find(x) == s.end()) {
                    colorSetCount[u]++;
                }
            }
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> k;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        g[u].push_back(v);
        g[v].push_back(u);
    }
    for (int i = 1; i <= n; i++) {
        cin >> c[i] >> d[i];
    }
    dfs(1, 0);
    for (int i = 1; i <= n; i++) {
        cout << dp[i] << "\n";
    }

    return 0;
}
