import java.io.*;
import java.util.*;

public class Main {
    static int N, K;
    static List<Integer>[] G;
    static int[][] color;
    static int[] dp;
    static int[] ans;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        K = Integer.parseInt(st.nextToken());
        G = new List[N + 1];
        for (int i = 1; i <= N; i++) {
            G[i] = new ArrayList<>();
        }
        for (int i = 1; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            int u = Integer.parseInt(st.nextToken());
            int v = Integer.parseInt(st.nextToken());
            G[u].add(v);
            G[v].add(u);
        }
        color = new int[N + 1][2];
        for (int i = 1; i <= N; i++) {
            st = new StringTokenizer(br.readLine());
            color[i][0] = Integer.parseInt(st.nextToken());
            color[i][1] = Integer.parseInt(st.nextToken());
        }
        dp = new int[N + 1];
        ans = new int[N + 1];
        dfs(1, 0);
        for (int i = 1; i <= N; i++) {
            System.out.println(ans[i]);
        }
    }

    static void dfs(int u, int fa) {
        int cnt = 0;
        for (int v : G[u]) {
            if (v == fa) continue;
            dfs(v, u);
            dp[u] = Math.max(dp[u], dp[v]);
            cnt++;
        }
        if (cnt == 0) {
            ans[u] = 1;
            dp[u] = 1;
        } else {
            int[] tmp = new int[K + 1];
            for (int v : G[u]) {
                if (v == fa) continue;
                tmp[dp[v]]++;
            }
            tmp[1]++;
            int res = 0;
            for (int i = 1; i <= K; i++) {
                if (tmp[i] > 0) {
                    res = Math.max(res, i + (tmp[i] - 1) * (K - 1));
                }
            }
            ans[u] = res + 1;
            dp[u] = Math.max(dp[u], res);
        }
    }
}
