import java.util.Scanner;

public class Main {
    static final int MOD = 65537;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int t = scanner.nextInt();
        while (t-- > 0) {
            int n = scanner.nextInt();
            int k = scanner.nextInt();
            int[] c = new int[n];
            for (int i = 0; i < n; i++) {
                c[i] = scanner.nextInt();
            }
            int[] a = new int[n];
            for (int i = n - 1; i >= 0; i--) {
                a[i] = (int) ((long) c[i] * pow(k, n - i, MOD) % MOD);
                for (int j = i - 1; j >= 0; j--) {
                    a[j] = (int) ((long) (c[j] - a[j + 1]) * pow(k, n - i - j, MOD) % MOD);
                }
            }
            for (int i = 0; i < n; i++) {
                System.out.print((a[i] + MOD) % MOD + " ");
            }
            System.out.println();
        }
    }

    static long pow(long a, long b, int mod) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % mod;
            }
            a = a * a % mod;
            b >>= 1;
        }
        return res;
    }
}
