def solve():
    H, W = read_list()
    grid = [read_list() for _ in range(H)]
    ans = float('inf')
    for i in range(H):
        for j in range(W):
            max1, min1 = float('-inf'), float('inf')
            max2, min2 = float('-inf'), float('inf')
            for x in range(H):
                for y in range(W):
                    if (x, y) == (i, j):
                        continue
                    if (x, y) == (i, j + 1):
                        max1 = max(max1, grid[x][y])
                        min1 = min(min1, grid[x][y])
                    elif (x, y) == (i + 1, j):
                        max2 = max(max2, grid[x][y])
                        min2 = min(min2, grid[x][y])
                    elif (x, y) == (i + 1, j + 1):
                        max1 = max(max1, grid[x][y])
                        min1 = min(min1, grid[x][y])
                    else:
                        max1 = max(max1, grid[x][y])
                        min1 = min(min1, grid[x][y])
                        max2 = max(max2, grid[x][y])
                        min2 = min(min2, grid[x][y])
            ans = min(ans, max(max1 - min1, max2 - min2))
    print(ans)

T = 1
for _ in range(T):
    solve()
