#include <stdio.h>
#include <stdlib.h>

// Function to find the union of two sets
int find(int* parent, int i) {
    if (parent[i] == i) {
        return i;
    }
    return find(parent, parent[i]);
}

// Function to union two sets
void union_sets(int* parent, int* rank, int x, int y) {
    int xroot = find(parent, x);
    int yroot = find(parent, y);

    if (rank[xroot] < rank[yroot]) {
        parent[xroot] = yroot;
    } else if (rank[xroot] > rank[yroot]) {
        parent[yroot] = xroot;
    } else {
        parent[yroot] = xroot;
        rank[xroot]++;
    }
}

int main() {
    int K = 100;
    int N = 1000;

    // Initialize parent array
    int* parent = (int*)malloc(N * sizeof(int));
    for (int i = 0; i < N; i++) {
        parent[i] = i;
    }

    // Initialize rank array
    int* rank = (int*)malloc(N * sizeof(int));
    for (int i = 0; i < N; i++) {
        rank[i] = 1;
    }

    // Perform K union operations
    for (int i = 0; i < K; i++) {
        int x = rand() % N;
        int y = rand() % N;
        union_sets(parent, rank, x, y);
    }

    // Perform K union operations
    for (int i = 0; i < K; i++) {
        int x = rand() % N;
        int y = rand() % N;
        union_sets(parent, rank, x, y);
    }

    // Check for cycles
    for (int i = 0; i < N; i++) {
        if (find(parent, i) != i) {
            printf("Cycle detected\n");
            return 0;
        }
    }

    printf("No cycles detected\n");
    free(parent);
    free(rank);
    return 0;
}
