#include <stdio.h>
#include <stdlib.h>

#define MAX 100000
#define K 100

int find(int* parent, int i) {
    if (parent[i] == i) {
        return i;
    }
    return find(parent, parent[i]);
}

void union_set(int* parent, int* rank, int x, int y) {
    int xroot = find(parent, x);
    int yroot = find(parent, y);

    if (xroot == yroot) {
        return;
    }

    if (rank[xroot] < rank[yroot]) {
        parent[xroot] = yroot;
    } else if (rank[xroot] > rank[yroot]) {
        parent[yroot] = xroot;
    } else {
        parent[yroot] = xroot;
        rank[xroot]++;
    }
}

int main() {
    int* parent = (int*)malloc((MAX + 1) * sizeof(int));
    int* rank = (int*)malloc((MAX + 1) * sizeof(int));

    for (int i = 0; i <= MAX; i++) {
        parent[i] = i;
        rank[i] = 0;
    }

    int n, m;
    scanf("%d %d", &n, &m);

    for (int i = 0; i < m; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        union_set(parent, rank, u, v);
    }

    int count = 0;
    for (int i = 0; i <= MAX; i++) {
        if (parent[i] == i) {
            count++;
        }
    }

    printf("%d\n", count - 1);

    free(parent);
    free(rank);

    return 0;
}
