#include <iostream>
#include <vector>

class UnionFind {
public:
    UnionFind(int n) : parent(n), rank(n, 1) {
        for (int i = 0; i < n; ++i) {
            parent[i] = i;
        }
    }

    int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    void unionSet(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);

        if (rootX != rootY) {
            if (rank[rootX] > rank[rootY]) {
                parent[rootY] = rootX;
            } else if (rank[rootX] < rank[rootY]) {
                parent[rootX] = rootY;
            } else {
                parent[rootY] = rootX;
                rank[rootX]++;
            }
        }
    }

private:
    std::vector<int> parent;
    std::vector<int> rank;
};

int main() {
    int n;
    std::cin >> n;

    UnionFind uf(n);

    for (int i = 0; i < n; ++i) {
        int x, y;
        std::cin >> x >> y;
        x--; y--; // adjust to 0-based index

        if (uf.find(x) != uf.find(y)) {
            uf.unionSet(x, y);
        }
    }

    int k;
    std::cin >> k;

    for (int i = 0; i < k; ++i) {
        int x, y;
        std::cin >> x >> y;
        x--; y--; // adjust to 0-based index

        if (uf.find(x) == uf.find(y)) {
            std::cout << "YES" << std::endl;
        } else {
            std::cout << "NO" << std::endl;
        }
    }

    return 0;
}
