class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)
        if root_x != root_y:
            if self.rank[root_x] > self.rank[root_y]:
                self.parent[root_y] = root_x
            else:
                self.parent[root_x] = root_y
                if self.rank[root_x] == self.rank[root_y]:
                    self.rank[root_y] += 1


def main():
    n = int(input())
    uf = UnionFind(n)
    for _ in range(n - 1):
        x, y = map(int, input().split())
        uf.union(x - 1, y - 1)

    for i in range(n):
        if uf.find(i) == i:
            print(i + 1, end=' ')
    print()


if __name__ == "__main__":
    main()
