#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX 100

typedef struct {
    char name[MAX];
    int age;
    char gender[MAX];
} Person;

void printPerson(Person p) {
    printf("Name: %s\n", p.name);
    printf("Age: %d\n", p.age);
    printf("Gender: %s\n", p.gender);
}

int main() {
    Person p1;
    strcpy(p1.name, "John");
    p1.age = 25;
    strcpy(p1.gender, "Male");

    Person p2;
    strcpy(p2.name, "Alice");
    p2.age = 30;
    strcpy(p2.gender, "Female");

    Person p3;
    strcpy(p3.name, "Bob");
    p3.age = 35;
    strcpy(p3.gender, "Male");

    Person people[] = {p1, p2, p3};
    int n = sizeof(people) / sizeof(people[0]);

    for (int i = 0; i < n; i++) {
        printPerson(people[i]);
        printf("\n");
    }

    return 0;
}
