#include <iostream>
#include <vector>
#include <string>
#include <sstream>

using namespace std;

class Solution {
public:
    int numSpecial(vector<vector<int>>& mat) {
        int m = mat.size();
        int n = mat[0].size();
        int rowSum[n] = {0};
        int colSum[m] = {0};

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                rowSum[j] += mat[i][j];
                colSum[i] += mat[i][j];
            }
        }

        int count = 0;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (mat[i][j] == rowSum[j] && mat[i][j] == colSum[i]) {
                    count++;
                }
            }
        }

        return count;
    }
};

int main() {
    Solution solution;
    vector<vector<int>> mat = {{1, 0, 0}, {0, 1, 0}, {0, 0, 1}};
    cout << solution.numSpecial(mat) << endl;

    return 0;
}
