#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int INF = numeric_limits<int>::max();

int main() {
    int H, W;
    cin >> H >> W;

    int A, B, C;
    cin >> A >> B >> C;

    int N;
    cin >> N;

    vector<int> S(N), T(N);
    for (int i = 0; i < N; i++) {
        cin >> S[i] >> T[i];
    }

    vector<vector<int>> dist(H + 1, vector<int>(W + 1, INF));
    vector<vector<int>> prev(H + 1, vector<int>(W + 1, -1));

    priority_queue<pair<int, pair<int, int>>, vector<pair<int, pair<int, int>>>, greater<pair<int, pair<int, int>>>> pq;
    pq.push({0, {S[N - 1], T[N - 1]}});
    dist[S[N - 1]][T[N - 1]] = 0;

    while (!pq.empty()) {
        auto [d, pos] = pq.top();
        pq.pop();

        if (d != dist[pos.first][pos.second]) continue;

        for (int i = 0; i < N; i++) {
            if (i == N - 1) continue;

            int x = pos.first;
            int y = pos.second;

            if (x > 0 && dist[x - 1][y] > d + C) {
                dist[x - 1][y] = d + C;
                prev[x - 1][y] = i;
                pq.push({dist[x - 1][y], {x - 1, y}});
            }

            if (y > 0 && dist[x][y - 1] > d + C) {
                dist[x][y - 1] = d + C;
                prev[x][y - 1] = i;
                pq.push({dist[x][y - 1], {x, y - 1}});
            }

            if (x < H && dist[x + 1][y] > d + C) {
                dist[x + 1][y] = d + C;
                prev[x + 1][y] = i;
                pq.push({dist[x + 1][y], {x + 1, y}});
            }

            if (y < W && dist[x][y + 1] > d + C) {
                dist[x][y + 1] = d + C;
                prev[x][y + 1] = i;
                pq.push({dist[x][y + 1], {x, y + 1}});
            }

            if (x > 0 && y > 0 && dist[x - 1][y - 1] > d + A * abs(S[i] - x) + B) {
                dist[x - 1][y - 1] = d + A * abs(S[i] - x) + B;
                prev[x - 1][y - 1] = i;
                pq.push({dist[x - 1][y - 1], {x - 1, y - 1}});
            }

            if (x > 0 && y < W && dist[x - 1][y + 1] > d + A * abs(S[i] - x) + B) {
                dist[x - 1][y + 1] = d + A * abs(S[i] - x) + B;
                prev[x - 1][y + 1] = i;
                pq.push({dist[x - 1][y + 1], {x - 1, y + 1}});
            }

            if (x < H && y > 0 && dist[x + 1][y - 1] > d + A * abs(S[i] - x) + B) {
                dist[x + 1][y - 1] = d + A * abs(S[i] - x) + B;
                prev[x + 1][y - 1] = i;
                pq.push({dist[x + 1][y - 1], {x + 1, y - 1}});
            }

            if (x < H && y < W && dist[x + 1][y + 1] > d + A * abs(S[i] - x) + B) {
                dist[x + 1][y + 1] = d + A * abs(S[i] - x) + B;
                prev[x + 1][y + 1] = i;
                pq.push({dist[x + 1][y + 1], {x + 1, y + 1}});
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < N; i++) {
        if (i == N - 1) continue;
        ans += dist[S[i]][T[i]];
    }

    cout << ans << endl;

    return 0;
}
