int matrix_chain_order(int *p, int n) {
    int i, j, k, l, q;
    int m[n + 1][n + 1];

    for (i = 1; i <= n; i++)
        m[i][i] = 0;

    for (l = 2; l <= n; l++) {
        for (i = 1; i <= n - l + 1; i++) {
            j = i + l - 1;
            m[i][j] = 1000000;
            for (k = i; k <= j - 1; k++) {
                q = m[i][k] + m[k + 1][j] + p[i - 1] * p[k] * p[j];
                if (q < m[i][j])
                    m[i][j] = q;
            }
        }
    }

    return m[1][n];
}

int main() {
    int n, i, r, c, p[MAX_N];
    int m[MAX_N][MAX_N];

    scanf("%d", &n);
    for (i = 1; i <= n; i++) {
        scanf("%d %d", &r, &c);
        p[i - 1] = r * c;
    }

    printf("%d\n", matrix_chain_order(p, n));

    return 0;
}
