#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int minMultiplications(vector<vector<int>>& dimensions, int n) {
    vector<vector<int>> m(n, vector<int>(n, 0));
    for (int i = 1; i < n; i++) {
        m[i][i] = 0;
    }

    for (int L = 2; L < n; L++) {
        for (int i = 1; i < n - L + 1; i++) {
            int j = i + L - 1;
            m[i][j] = INT_MAX;
            for (int k = i; k < j; k++) {
                int q = m[i][k] + m[k + 1][j] + dimensions[i - 1][0] * dimensions[k][1] * dimensions[j][1];
                m[i][j] = min(m[i][j], q);
            }
        }
    }

    return m[1][n - 1];
}

int main() {
    int n;
    cin >> n;
    vector<vector<int>> dimensions(n, vector<int>(2));
    for (int i = 0; i < n; i++) {
        cin >> dimensions[i][0] >> dimensions[i][1];
    }

    cout << minMultiplications(dimensions, n) << endl;

    return 0;
}
