#include <stdio.h>
#include <string.h>

#define MAXLEN 1000

int lcs(int m, int n, char *x, char *y) {
    int i, j;
    int dp[MAXLEN + 1][MAXLEN + 1];

    for (i = 0; i <= m; i++) {
        for (j = 0; j <= n; j++) {
            if (i == 0 || j == 0)
                dp[i][j] = 0;
            else if (x[i - 1] == y[j - 1])
                dp[i][j] = dp[i - 1][j - 1] + 1;
            else
                dp[i][j] = (dp[i - 1][j] > dp[i][j - 1]) ? dp[i - 1][j] : dp[i][j - 1];
        }
    }

    return dp[m][n];
}

int main() {
    int q;
    scanf("%d", &q);

    while (q--) {
        char x[MAXLEN + 1], y[MAXLEN + 1];
        scanf("%s %s", x, y);
        int m = strlen(x);
        int n = strlen(y);
        printf("%d\n", lcs(m, n, x, y));
    }

    return 0;
}
