import java.io.*;
import java.util.*;

public class Main {
    static int N, H, W, A, B, C;
    static int[][] S = new int[100005][2];
    static int[][] dp = new int[100005][100005];
    static int[][] pre = new int[100005][100005];
    static int[][] nxt = new int[100005][100005];
    static int[][] pre2 = new int[100005][100005];
    static int[][] nxt2 = new int[100005][100005];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        H = Integer.parseInt(st.nextToken());
        W = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        A = Integer.parseInt(st.nextToken());
        B = Integer.parseInt(st.nextToken());
        C = Integer.parseInt(st.nextToken());
        N = Integer.parseInt(br.readLine());
        for (int i = 1; i <= N; i++) {
            st = new StringTokenizer(br.readLine());
            S[i][0] = Integer.parseInt(st.nextToken());
            S[i][1] = Integer.parseInt(st.nextToken());
        }
        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= N; j++) {
                dp[i][j] = (int) 1e9;
                pre[i][j] = (int) 1e9;
                nxt[i][j] = (int) 1e9;
                pre2[i][j] = (int) 1e9;
                nxt2[i][j] = (int) 1e9;
            }
        }
        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= N; j++) {
                if (i == j) continue;
                int x = Math.abs(S[i][0] - S[j][0]);
                int y = Math.abs(S[i][1] - S[j][1]);
                if (x + y == 0) {
                    dp[i][j] = 0;
                    pre[i][j] = 0;
                    nxt[i][j] = 0;
                    pre2[i][j] = 0;
                    nxt2[i][j] = 0;
                } else {
                    for (int k = 1; k <= x + y; k++) {
                        int a = Math.min(x, k);
                        int b = Math.min(y, k);
                        int c = k - a - b;
                        int d = Math.max(x - a, y - b);
                        if (d <= 0) {
                            dp[i][j] = Math.min(dp[i][j], (a + b) * C + (c + d) * A * Math.max(a, b) + B * Math.max(a, b));
                            pre[i][j] = Math.min(pre[i][j], (a + b) * C + (c + d) * A * a + B * a);
                            nxt[i][j] = Math.min(nxt[i][j], (a + b) * C + (c + d) * A * b + B * b);
                            pre2[i][j] = Math.min(pre2[i][j], (a + b) * C + (c + d) * A * Math.max(a, b) + B * Math.max(a, b));
                            nxt2[i][j] = Math.min(nxt2[i][j], (a + b) * C + (c + d) * A * Math.max(a, b) + B * Math.max(a, b));
                        }
                    }
                }
            }
        }
        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= N; j++) {
                if (i == j) continue;
                for (int k = 1; k <= N; k++) {
                    if (k == i || k == j) continue;
                    dp[i][j] = Math.min(dp[i][j], dp[i][k] + dp[k][j]);
                    pre[i][j] = Math.min(pre[i][j], pre[i][k] + dp[k][j]);
                    nxt[i][j] = Math.min(nxt[i][j], dp[i][k] + nxt[k][j]);
                    pre2[i][j] = Math.min(pre2[i][j], pre[i][k] + pre2[k][j]);
                    nxt2[i][j] = Math.min(nxt2[i][j], nxt[i][k] + nxt2[k][j]);
                }
            }
        }
        int ans = (int) 1e9;
        for (int i = 1; i <= N; i++) {
            if (i == N) continue;
            ans = Math.min(ans, dp[N][i] + pre2[N][i]);
        }
        System.out.println(ans);
    }
}
