#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

const int MAXN = 505;

int n;
double p[MAXN], q[MAXN];
double dp[MAXN][MAXN];
double f[MAXN][MAXN];

void solve() {
    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> p[i];
    }
    for (int i = 0; i <= n; i++) {
        cin >> q[i];
    }

    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = 1e9;
        }
    }

    for (int i = 0; i <= n; i++) {
        dp[i][i] = 0;
    }

    for (int len = 1; len <= n; len++) {
        for (int l = 0; l <= n - len; l++) {
            int r = l + len - 1;
            for (int k = l; k <= r; k++) {
                dp[l][r] = min(dp[l][r], dp[l][k] + dp[k + 1][r] + p[l] * (k - l + 1) + q[r + 1] * (r - k + 1));
            }
        }
    }

    cout << fixed << setprecision(4) << dp[0][n] << endl;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    solve();
    return 0;
}
