import java.io.*;
import java.util.*;

public class Main {
    static final int N = 505;
    static double[][] f = new double[N][N];
    static double[] p = new double[N];
    static double[] q = new double[N];
    static double[][] g = new double[N][N];
    static double[][] h = new double[N][N];
    static double[] min = new double[N];
    static double[] pre = new double[N];
    static double[] suf = new double[N];

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        for (int i = 1; i <= n; i++) {
            p[i] = sc.nextDouble();
        }
        for (int i = 0; i <= n; i++) {
            q[i] = sc.nextDouble();
        }
        for (int i = 1; i <= n; i++) {
            f[i][i] = p[i];
        }
        for (int i = 0; i <= n; i++) {
            f[i][i] += q[i];
        }
        for (int len = 2; len <= n; len++) {
            for (int l = 1; l + len - 1 <= n; l++) {
                int r = l + len - 1;
                f[l][r] = 1e9;
                for (int k = l; k <= r; k++) {
                    double val = f[l][k - 1] + f[k + 1][r] + p[l] * (k - l + 1) + q[r] * (r - k + 1);
                    if (val < f[l][r]) {
                        f[l][r] = val;
                        g[l][r] = k;
                    }
                }
            }
        }
        int k = (int) g[1][n];
        for (int i = 1; i <= n; i++) {
            if (i <= k) {
                pre[i] = f[1][i - 1] + p[i];
            } else {
                pre[i] = f[1][k - 1] + f[k + 1][i] + p[i];
            }
        }
        for (int i = n; i >= 1; i--) {
            if (i >= k) {
                suf[i] = f[i + 1][n] + q[i];
            } else {
                suf[i] = f[i + 1][k - 1] + f[k + 1][n] + q[i];
            }
        }
        for (int i = 1; i <= n; i++) {
            min[i] = Math.min(pre[i], suf[i]);
        }
        double ans = 0;
        for (int i = 1; i <= n; i++) {
            ans += min[i];
        }
        System.out.printf("%.4f\n", ans);
    }
}
