            cost += dp(i, k) + dp(k + 1, j) + p[k] + q[x]
        memo[i][j] = min(memo[i][j], cost)
    return memo[i][j]

def build(i, j):
    if i > j:
        return []
    res = []
    for x in range(i, j + 1):
        cost = 0
        for k in range(i, x):
            cost += dp(i, k) + dp(k + 1, x - 1) + p[k] + q[x]
        for k in range(x, j):
            cost += dp(i, k) + dp(k + 1, j) + p[k] + q[x]
        if cost == memo[i][j]:
            res.append(x)
            if x == i:
                res.extend(build(i + 1, j))
            elif x == j:
                res.extend(build(i, j - 1))
            else:
                res.extend(build(i, x - 1))
                res.extend(build(x + 1, j))
            return res
    return res

