#include <stdio.h>
#include <stdlib.h>

#define MAX_VERTICES 100

int main() {
    int n;
    scanf("%d", &n);

    int** adj_list = (int**)malloc(n * sizeof(int*));
    for (int i = 0; i < n; i++) {
        adj_list[i] = (int*)malloc(2 + 2 * MAX_VERTICES * sizeof(int));
    }

    int** adj_matrix = (int**)malloc(n * sizeof(int*));
    for (int i = 0; i < n; i++) {
        adj_matrix[i] = (int*)malloc(n * sizeof(int));
    }

    for (int i = 0; i < n; i++) {
        scanf("%d %d", &adj_list[i][0], &adj_list[i][1]);
        for (int j = 2; j < 2 + 2 * adj_list[i][1]; j += 2) {
            adj_list[i][j] = j / 2 - 1;
            adj_matrix[i][adj_list[i][j]] = 1;
        }
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            printf("%d ", adj_matrix[i][j]);
        }
        printf("\n");
    }

    for (int i = 0; i < n; i++) {
        free(adj_list[i]);
        free(adj_matrix[i]);
    }
    free(adj_list);
    free(adj_matrix);

    return 0;
}
