#include <iostream>
#include <vector>
#include <string>
#include <sstream>

int main() {
    int n;
    std::cin >> n;

    std::vector<std::vector<int>> adj(n + 1);
    for (int i = 1; i <= n; i++) {
        int k;
        std::cin >> k;
        for (int j = 0; j < k; j++) {
            int v;
            std::cin >> v;
            adj[i].push_back(v);
        }
    }

    std::vector<std::vector<int>> matrix(n + 1, std::vector<int>(n + 1, 0));
    for (int i = 1; i <= n; i++) {
        for (int j : adj[i]) {
            matrix[i][j] = 1;
        }
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            std::cout << matrix[i][j] << " ";
        }
        std::cout << std::endl;
    }

    return 0;
}
