#include <stdio.h>
#include <stdlib.h>

#define MAX_VERTICES 100

typedef struct {
    int id;
    int d;
    int f;
} Vertex;

int main() {
    int n;
    scanf("%d", &n);

    Vertex vertices[MAX_VERTICES];
    for (int i = 0; i < n; i++) {
        vertices[i].id = i + 1;
        vertices[i].d = 0;
        vertices[i].f = 0;
    }

    int visited[MAX_VERTICES] = {0};
    int time = 1;

    for (int i = 0; i < n; i++) {
        int u, k;
        scanf("%d %d", &u, &k);
        for (int j = 0; j < k; j++) {
            int v;
            scanf("%d", &v);
            if (!visited[v - 1]) {
                visited[v - 1] = 1;
                vertices[v - 1].d = time++;
                for (int w = 0; w < n; w++) {
                    if (vertices[w].d == 0 && w < u - 1) {
                        vertices[w].d = time++;
                    }
                }
                vertices[u - 1].f = time++;
                for (int w = 0; w < n; w++) {
                    if (vertices[w].d != 0 && vertices[w].f == 0 && w > u - 1) {
                        vertices[w].f = time++;
                    }
                }
            }
        }
    }

    for (int i = 0; i < n; i++) {
        printf("%d %d %d\n", vertices[i].id, vertices[i].d, vertices[i].f);
    }

    return 0;
}
