#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

struct Vertex {
    int id;
    int d;
    int f;
};

bool compareVertices(const Vertex& a, const Vertex& b) {
    return a.id < b.id;
}

int main() {
    int n;
    cin >> n;

    vector<vector<int>> graph(n + 1);
    vector<Vertex> vertices(n + 1);

    for (int i = 1; i <= n; ++i) {
        int u, k;
        cin >> u >> k;
        vertices[u].id = u;
        for (int j = 0; j < k; ++j) {
            int v;
            cin >> v;
            graph[u].push_back(v);
        }
    }

    int time = 1;
    for (int i = 1; i <= n; ++i) {
        if (vertices[i].d == 0) {
            queue<int> q;
            q.push(i);
            vertices[i].d = time++;
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v : graph[u]) {
                    if (vertices[v].d == 0) {
                        q.push(v);
                        vertices[v].d = time++;
                    }
                    vertices[u].f = max(vertices[u].f, vertices[v].f);
                }
                vertices[u].f = time++;
            }
        }
    }

    sort(vertices.begin() + 1, vertices.end(), compareVertices);

    for (int i = 1; i <= n; ++i) {
        cout << vertices[i].id << " " << vertices[i].d << " " << vertices[i].f << endl;
    }

    return 0;
}
