import java.util.*;

public class Main {
    static class Graph {
        int V;
        List<List<Integer>> adj;

        public Graph(int V) {
            this.V = V;
            adj = new ArrayList<>();
            for (int i = 0; i < V; i++) {
                adj.add(new ArrayList<>());
            }
        }

        public void addEdge(int u, int v) {
            adj.get(u).add(v);
        }
    }

    static class Vertex {
        int id;
        int d;
        int f;

        public Vertex(int id) {
            this.id = id;
            this.d = 0;
            this.f = 0;
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        Graph graph = new Graph(n);
        for (int i = 0; i < n; i++) {
            int u = scanner.nextInt();
            int k = scanner.nextInt();
            for (int j = 0; j < k; j++) {
                int v = scanner.nextInt();
                graph.addEdge(u - 1, v - 1);
            }
        }
        int[] timestamp = new int[n];
        Arrays.fill(timestamp, -1);
        for (int i = 0; i < n; i++) {
            if (timestamp[i] == -1) {
                dfs(graph, i, timestamp);
            }
        }
        for (int i = 0; i < n; i++) {
            System.out.println((i + 1) + " " + (timestamp[i] + 1) + " " + (timestamp[i] + graph.adj.get(i).size()));
        }
    }

    static void dfs(Graph graph, int u, int[] timestamp) {
        timestamp[u] = graph.adj.get(u).size();
        for (int v : graph.adj.get(u)) {
            if (timestamp[v] == -1) {
                dfs(graph, v, timestamp);
            }
            timestamp[u]++;
        }
        timestamp[u]++;
    }
}
