from collections import defaultdict, deque

def dfs(graph):
    n = len(graph)
    timestamps = [0] * n
    stack = deque()
    time = 1

    for i in range(n):
        if timestamps[i] == 0:
            dfs_helper(graph, i, stack, timestamps, time)

    for i in range(n):
        print(f"{i+1} {timestamps[i]} {timestamps[i] + 1}")

def dfs_helper(graph, vertex, stack, timestamps, time):
    timestamps[vertex] = time
    time += 1
    stack.append(vertex)

    for neighbor in graph[vertex]:
        if timestamps[neighbor] == 0:
            dfs_helper(graph, neighbor, stack, timestamps, time)
        elif neighbor in stack:
            while stack and stack[-1] == neighbor:
                stack.pop()

    stack.pop()
    timestamps[vertex] += 1

def main():
    n = int(input())
    graph = defaultdict(list)

    for _ in range(n):
        u, k, *neighbors = map(int, input().split())
        graph[u-1] = neighbors

    dfs(graph)

if __name__ == "__main__":
    main()
