#include <stdio.h>
#include <stdlib.h>

#define MAX_VERTICES 100
#define INF 999999

int main() {
    int n, u, k, v, i, j, d[MAX_VERTICES], visited[MAX_VERTICES], distance[MAX_VERTICES];
    int adj[MAX_VERTICES][MAX_VERTICES];

    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d %d", &u, &k);
        for (j = 0; j < k; j++) {
            scanf("%d", &v);
            adj[u][v] = 1;
        }
    }

    for (i = 0; i < n; i++) {
        distance[i] = INF;
        visited[i] = 0;
    }
    distance[0] = 0;

    for (i = 0; i < n; i++) {
        int min_distance = INF, min_index = -1;
        for (j = 0; j < n; j++) {
            if (!visited[j] && distance[j] < min_distance) {
                min_distance = distance[j];
                min_index = j;
            }
        }
        if (min_index == -1) break;
        visited[min_index] = 1;
        for (j = 0; j < n; j++) {
            if (adj[min_index][j] == 1) {
                if (!visited[j]) {
                    distance[j] = distance[min_index] + 1;
                }
            }
        }
    }

    for (i = 0; i < n; i++) {
        if (distance[i] == INF) {
            printf("%d -1\n", i + 1);
        } else {
            printf("%d %d\n", i + 1, distance[i]);
        }
    }

    return 0;
}
