#include <stdio.h>
#include <stdlib.h>

#define MAX_N 100000
#define MAX_M 100000

int parent[MAX_N];

int find(int x) {
    if (parent[x] == x) return x;
    return parent[x] = find(parent[x]);
}

void union_set(int x, int y) {
    int root_x = find(x);
    int root_y = find(y);
    if (root_x != root_y) parent[root_x] = root_y;
}

int main() {
    int n, m, q;
    scanf("%d %d", &n, &m);
    for (int i = 0; i < n; i++) parent[i] = i;
    for (int i = 0; i < m; i++) {
        int x, y;
        scanf("%d %d", &x, &y);
        union_set(x, y);
    }
    scanf("%d", &q);
    for (int i = 0; i < q; i++) {
        int x, y;
        scanf("%d %d", &x, &y);
        if (find(x) == find(y)) printf("yes\n");
        else printf("no\n");
    }
    return 0;
}
