from collections import defaultdict

def dfs(node, parent, graph, visited):
    visited[node] = True
    for neighbor in graph[node]:
        if not visited[neighbor]:
            dfs(neighbor, node, graph, visited)

def connected_components(n, m, relations, queries):
    graph = defaultdict(list)
    for u, v in relations:
        graph[u].append(v)
        graph[v].append(u)

    visited = [False] * n
    for i in range(n):
        if not visited[i]:
            dfs(i, -1, graph, visited)

    results = []
    for s, t in queries:
        if visited[s] and visited[t] and s != t:
            results.append("yes")
        else:
            results.append("no")

    return results

n, m = map(int, input().split())
relations = [tuple(map(int, input().split())) for _ in range(m)]
q = int(input())
queries = [tuple(map(int, input().split())) for _ in range(q)]

results = connected_components(n, m, relations, queries)
for result in results:
    print(result)
