#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 1000000
#define M 1000000

int n, m;
int c[N + 1];
int cnt[M + 1];
int dp[M + 1][N + 1];

void solve() {
    scanf("%d %d", &n, &m);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &c[i]);
        cnt[c[i]]++;
    }

    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            dp[i][j] = 1e9;
        }
    }

    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            if (c[j] == i) {
                dp[i][j] = 0;
            }
        }
    }

    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            if (c[j] == i) {
                for (int k = 1; k < j; k++) {
                    if (c[k] == i) {
                        dp[i][j] = dp[i][j] < dp[i][k] + j - k ? dp[i][j] : dp[i][k] + j - k;
                    }
                }
                for (int k = j + 1; k <= n; k++) {
                    if (c[k] == i) {
                        dp[i][j] = dp[i][j] < dp[i][k] + k - j + 1 : dp[i][j] : dp[i][k] + k - j + 1;
                    }
                }
            }
        }
    }

    for (int i = 1; i <= m; i++) {
        int ans = 1e9;
        for (int j = 1; j <= n; j++) {
            ans = ans < dp[i][j] + cnt[i] - 1 ? ans : dp[i][j] + cnt[i] - 1;
        }
        printf("%d\n", ans);
    }
}

int main() {
    int T = 1;
    while (T--) {
        solve();
    }
    return 0;
}
