#include <stdio.h>
#include <stdlib.h>

#define MAX_VERTICES 100
#define INF 1000000

int min(int a, int b) {
    return (a < b) ? a : b;
}

void printMST(int parent[], int n) {
    printf("Edge \tWeight\n");
    for (int i = 1; i < n; i++) {
        printf("%d - %d \t%d\n", parent[i], i, (int)graph[i][parent[i]]);
    }
}

void primMST(int graph[MAX_VERTICES][MAX_VERTICES], int n) {
    int *parent = (int *)malloc(n * sizeof(int));
    int *key = (int *)malloc(n * sizeof(int));
    int *mstSet = (int *)malloc(n * sizeof(int));

    for (int i = 0; i < n; i++) {
        key[i] = INF;
        mstSet[i] = 0;
    }

    key[0] = 0;
    parent[0] = -1;

    for (int cout = 0; cout < n - 1; cout++) {
        int u = -1;
        for (int v = 0; v < n; v++) {
            if (mstSet[v] == 0 && key[v] != INF && (u == -1 || key[v] < key[u]))
                u = v;
        }

        mstSet[u] = 1;

        for (int v = 0; v < n; v++) {
            if (graph[u][v] > 0 && mstSet[v] == 0 && key[v] > graph[u][v]) {
                key[v] = graph[u][v];
                parent[v] = u;
            }
        }
    }

    printMST(parent, n);
    int totalWeight = 0;
    for (int i = 1; i < n; i++) {
        totalWeight += graph[i][parent[i]];
    }
    printf("\nTotal weight of MST = %d\n", totalWeight);
}

int main() {
    int n;
    scanf("%d", &n);

    int graph[MAX_VERTICES][MAX_VERTICES];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            scanf("%d", &graph[i][j]);
        }
    }

    primMST(graph, n);

    return 0;
}
