#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 105;

int n;
vector<pair<int, int>> edges;
vector<vector<int>> adj;
int parent[MAXN];

int find(int x) {
    if (parent[x] != x) {
        parent[x] = find(parent[x]);
    }
    return parent[x];
}

void unionSet(int x, int y) {
    int rootX = find(x);
    int rootY = find(y);
    if (rootX != rootY) {
        parent[rootX] = rootY;
    }
}

int kruskal() {
    sort(edges.begin(), edges.end());
    int totalWeight = 0;
    for (auto& edge : edges) {
        int u = edge.first;
        int v = edge.second;
        if (find(u) != find(v)) {
            unionSet(u, v);
            totalWeight += edge.first;
        }
    }
    return totalWeight;
}

int main() {
    cin >> n;
    adj.resize(n + 1);
    for (int i = 1; i <= n; i++) {
        adj[i].resize(n + 1);
        for (int j = 1; j <= n; j++) {
            cin >> adj[i][j];
            if (adj[i][j] != -1) {
                edges.push_back({adj[i][j], i, j});
            }
        }
    }
    parent[1] = 1;
    for (int i = 2; i <= n; i++) {
        parent[i] = i;
    }
    cout << kruskal() << endl;
    return 0;
}
