import sys
import heapq

def min_spanning_tree(graph):
    n = len(graph)
    visited = [False] * n
    visited[0] = True
    mst_weight = 0
    edges = []

    for i in range(n):
        for j in range(i + 1, n):
            if graph[i][j] != -1:
                heapq.heappush(edges, (graph[i][j], i, j))

    while edges:
        weight, u, v = heapq.heappop(edges)
        if not visited[v]:
            visited[v] = True
            mst_weight += weight

            for i in range(n):
                if graph[v][i] != -1 and not visited[i]:
                    heapq.heappush(edges, (graph[v][i], v, i))

    return mst_weight

def main():
    n = int(input())
    graph = []
    for _ in range(n):
        row = list(map(int, input().split()))
        graph.append(row)

    print(min_spanning_tree(graph))

if __name__ == "__main__":
    main()
