#include <stdio.h>
#include <limits.h>

#define N 100
#define INF INT_MAX

int min(int a, int b) {
    return (a < b) ? a : b;
}

void dijkstra(int n, int adj[N][N], int dist[N]) {
    int i, j, k, u, v, w;
    int visited[N] = {0};

    for (i = 0; i < n; i++)
        dist[i] = INF;

    dist[0] = 0;

    for (i = 0; i < n; i++) {
        int min_dist = INF;
        int min_index = -1;

        for (u = 0; u < n; u++) {
            if (!visited[u] && dist[u] < min_dist) {
                min_dist = dist[u];
                min_index = u;
            }
        }

        visited[min_index] = 1;

        for (k = 0; k < adj[min_index][0]; k++) {
            v = adj[min_index][k + 1];
            w = adj[min_index][k + 2];

            if (!visited[v] && dist[min_index] + w < dist[v])
                dist[v] = dist[min_index] + w;
        }
    }
}

int main() {
    int n, i, j, k, u, v, w;
    int adj[N][N];
    int dist[N];

    scanf("%d", &n);

    for (i = 0; i < n; i++) {
        scanf("%d %d", &u, &k);
        adj[u][0] = k;

        for (j = 0; j < k; j++) {
            scanf("%d %d", &v, &w);
            adj[u][j + 1] = v;
            adj[u][j + 2] = w;
        }
    }

    dijkstra(n, adj, dist);

    for (i = 0; i < n; i++)
        printf("%d %d\n", i, dist[i]);

    return 0;
}
