#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int MAX_VERTICES = 100;
const int MAX_EDGES = 10000;

struct Edge {
    int destination;
    int weight;
};

int main() {
    int n;
    cin >> n;

    vector<vector<Edge>> graph(n);
    vector<int> distance(n, numeric_limits<int>::max());
    distance[0] = 0;

    for (int i = 0; i < n; i++) {
        int u;
        cin >> u;

        int k;
        cin >> k;

        for (int j = 0; j < k; j++) {
            int v;
            int c;
            cin >> v >> c;

            graph[u].push_back({v, c});
        }
    }

    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
    pq.push({0, 0});

    while (!pq.empty()) {
        int d, u;
        tie(d, u) = pq.top();
        pq.pop();

        if (d != distance[u]) {
            continue;
        }

        for (const auto& edge : graph[u]) {
            int v = edge.destination;
            int c = edge.weight;

            if (distance[v] > distance[u] + c) {
                distance[v] = distance[u] + c;
                pq.push({distance[v], v});
            }
        }
    }

    for (int i = 0; i < n; i++) {
        cout << i << " " << distance[i] << endl;
    }

    return 0;
}
