import sys
import heapq

def dijkstra(n, edges):
    graph = [[] for _ in range(n)]
    for u, k, *rest in edges:
        for i in range(0, len(rest), 2):
            v, c = rest[i], rest[i+1]
            graph[u].append((v, c))

    distances = [sys.maxsize] * n
    distances[0] = 0
    pq = [(0, 0)]

    while pq:
        d, u = heapq.heappop(pq)
        if d > distances[u]:
            continue
        for v, c in graph[u]:
            if distances[v] > distances[u] + c:
                distances[v] = distances[u] + c
                heapq.heappush(pq, (distances[v], v))

    return distances

def main():
    n = int(input())
    edges = []
    for _ in range(n):
        u, k, *rest = map(int, input().split())
        edges.append((u, k, *rest))

    distances = dijkstra(n, edges)
    for i in range(n):
        print(i, distances[i])

if __name__ == "__main__":
    main()
