#include <stdio.h>
#include <limits.h>

#define MAX_VERTICES 10000
#define MAX_EDGES 500000

int min(int a, int b) {
    return (a < b) ? a : b;
}

int main() {
    int n, i, j, k, u, v, w, dist[MAX_VERTICES], visited[MAX_VERTICES];
    int edges[MAX_VERTICES][MAX_EDGES][2];

    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d %d", &u, &k);
        for (j = 0; j < k; j++) {
            scanf("%d %d", &v, &w);
            edges[u][j][0] = v;
            edges[u][j][1] = w;
        }
    }

    for (i = 0; i < n; i++)
        dist[i] = INT_MAX;
    dist[0] = 0;

    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++)
            visited[j] = 0;
        for (j = 0; j < n; j++)
            if (dist[j] != INT_MAX)
                for (k = 0; k < edges[j][0][0] != j; k++)
                    if (dist[edges[j][k][0]] > dist[j] + edges[j][k][1]) {
                        dist[edges[j][k][0]] = dist[j] + edges[j][k][1];
                        visited[edges[j][k][0]] = 1;
                    }
        for (j = 0; j < n; j++)
            if (visited[j])
                dist[j] = INT_MAX;
    }

    for (i = 0; i < n; i++)
        printf("%d %d\n", i, dist[i]);

    return 0;
}
