#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int MAX_VERTICES = 10000;
const int MAX_EDGES = 500000;

struct Edge {
    int destination;
    int weight;
};

struct Vertex {
    vector<Edge> edges;
    int distance;
};

int main() {
    int numVertices;
    cin >> numVertices;

    vector<Vertex> vertices(numVertices);

    for (int i = 0; i < numVertices; i++) {
        int degree;
        cin >> degree;

        for (int j = 0; j < degree; j++) {
            int destination;
            int weight;
            cin >> destination >> weight;

            vertices[i].edges.push_back({destination, weight});
        }
    }

    for (int i = 0; i < numVertices; i++) {
        vertices[i].distance = numeric_limits<int>::max();
    }

    vertices[0].distance = 0;

    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> queue;
    queue.push({0, 0});

    while (!queue.empty()) {
        int currentVertex = queue.top().second;
        int currentDistance = queue.top().first;
        queue.pop();

        if (currentDistance > vertices[currentVertex].distance) {
            continue;
        }

        for (const auto& edge : vertices[currentVertex].edges) {
            int newDistance = currentDistance + edge.weight;

            if (newDistance < vertices[edge.destination].distance) {
                vertices[edge.destination].distance = newDistance;
                queue.push({newDistance, edge.destination});
            }
        }
    }

    for (int i = 0; i < numVertices; i++) {
        cout << i << " " << vertices[i].distance << endl;
    }

    return 0;
}
