#include <iostream>
#include <vector>
#include <algorithm>
#include <cstring>

using namespace std;

const int N = 1e6 + 5;

int n, m;
int c[N];
int cnt[N];
int dp[N][N];
int ans[N];

int main() {
    cin >> n >> m;
    for (int i = 1; i <= n; i++) {
        cin >> c[i];
        cnt[c[i]]++;
    }
    for (int i = 1; i <= m; i++) {
        dp[i][0] = 0;
        dp[i][1] = cnt[i];
    }
    for (int i = 2; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            dp[j][i] = dp[j][i - 1];
            int l = 1, r = i - 1;
            while (l < r) {
                int mid = (l + r + 1) / 2;
                if (c[i - mid + 1] == j) {
                    l = mid;
                } else {
                    r = mid - 1;
                }
            }
            if (c[i - l + 1] == j) {
                dp[j][i] = max(dp[j][i], dp[j][i - l] + (i - l) * (i - l + 1) / 2);
            }
            l = i - 1, r = 1;
            while (l > r) {
                int mid = (l + r) / 2;
                if (c[i - mid + 1] == j) {
                    r = mid;
                } else {
                    l = mid + 1;
                }
            }
            if (c[i - r + 1] == j) {
                dp[j][i] = max(dp[j][i], dp[j][i - r] + (i - r) * (i - r + 1) / 2);
            }
        }
    }
    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            ans[i] = max(ans[i], dp[i][j]);
        }
    }
    for (int i = 1; i <= m; i++) {
        cout << ans[i] << endl;
    }
    return 0;
}
