#include <stdio.h>
#include <stdlib.h>

#define N 8

int place_queens(int board[N][N], int row, int col, int k) {
    if (row == N) {
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                printf("%c", board[i][j] ? 'Q' : '.');
            }
            printf("\n");
        }
        exit(0);
    }

    for (int c = 0; c < N; c++) {
        if (is_safe(board, row, c, k)) {
            board[row][c] = 1;
            if (place_queens(board, row + 1, 0, k)) {
                return 1;
            }
            board[row][c] = 0;
        }
    }
    return 0;
}

int is_safe(int board[N][N], int row, int col, int k) {
    for (int i = 0; i < row; i++) {
        if (board[i][col]) {
            return 0;
        }
        for (int j = 0; j < N; j++) {
            if (board[i][j] && (abs(i - row) == abs(j - col))) {
                return 0;
            }
        }
    }
    return 1;
}

int main() {
    int k;
    scanf("%d", &k);
    int board[N][N] = {0};
    for (int i = 0; i < k; i++) {
        int r, c;
        scanf("%d %d", &r, &c);
        board[r][c] = 1;
    }
    place_queens(board, 0, 0, k);
    return 0;
}
