#include <iostream>
#include <vector>
#include <string>

using namespace std;

void printBoard(const vector<string>& board) {
    for (const auto& row : board) {
        cout << row << endl;
    }
}

bool isSafe(const vector<string>& board, int row, int col) {
    for (int i = 0; i < row; ++i) {
        if (board[i][col] == 'Q') {
            return false;
        }
    }
    for (int i = row, j = col; i >= 0 && j >= 0; --i, --j) {
        if (board[i][j] == 'Q') {
            return false;
        }
    }
    for (int i = row, j = col; i >= 0 && j < board.size(); --i, ++j) {
        if (board[i][j] == 'Q') {
            return false;
        }
    }
    return true;
}

void solve(int row, vector<string>& board, vector<pair<int, int>>& queens) {
    if (row == board.size()) {
        printBoard(board);
        return;
    }
    for (int col = 0; col < board.size(); ++col) {
        if (isSafe(board, row, col)) {
            board[row][col] = 'Q';
            queens.push_back({row, col});
            solve(row + 1, board, queens);
            board[row][col] = '.';
            queens.pop_back();
        }
    }
}

int main() {
    int k;
    cin >> k;
    vector<string> board(8, string(8, '.'));
    vector<pair<int, int>> queens;
    for (int i = 0; i < k; ++i) {
        int r, c;
        cin >> r >> c;
        board[r][c] = 'Q';
        queens.push_back({r, c});
    }
    solve(0, board, queens);
    return 0;
}
