import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int k = scanner.nextInt();
        int[][] queens = new int[k][2];
        for (int i = 0; i < k; i++) {
            queens[i][0] = scanner.nextInt();
            queens[i][1] = scanner.nextInt();
        }
        solve(queens, 0, new int[8][8], new boolean[8][8]);
    }

    public static void solve(int[][] queens, int index, int[][] board, boolean[][] placed) {
        if (index == 8) {
            printBoard(board);
            return;
        }
        for (int i = 0; i < 8; i++) {
            if (isSafe(queens, index, i, placed)) {
                board[index][i] = 1;
                placed[index][i] = true;
                solve(queens, index + 1, board, placed);
                board[index][i] = 0;
                placed[index][i] = false;
            }
        }
    }

    public static boolean isSafe(int[][] queens, int row, int col, boolean[][] placed) {
        for (int i = 0; i < row; i++) {
            if (placed[i][col]) {
                return false;
            }
            if (queens[i][0] == row || queens[i][1] == col || Math.abs(queens[i][0] - row) == Math.abs(queens[i][1] - col)) {
                return false;
            }
        }
        return true;
    }

    public static void printBoard(int[][] board) {
        for (int i = 0; i < 8; i++) {
            for (int j = 0; j < 8; j++) {
                if (board[i][j] == 1) {
                    System.out.print("Q");
                } else {
                    System.out.print(".");
                }
            }
            System.out.println();
        }
    }
}
