def print_board(board):
    for row in board:
        print(''.join(row))

def is_safe(board, row, col):
    for i in range(row):
        if board[i][col] == 'Q':
            return False
        if col - (row - i) >= 0 and board[i][col - (row - i)] == 'Q':
            return False
        if col + (row - i) < 8 and board[i][col + (row - i)] == 'Q':
            return False
    return True

def solve_n_queens(n, k, board, row):
    if row == n:
        return True
    if row >= k:
        return False
    for col in range(8):
        if is_safe(board, row, col):
            board[row][col] = 'Q'
            if solve_n_queens(n, k, board, row + 1):
                return True
            board[row][col] = '.'
    return False

def find_solution(k, queens):
    board = [['.' for _ in range(8)] for _ in range(8)]
    for r, c in queens:
        board[r][c] = 'Q'
    solve_n_queens(8, k, board, 0)
    print_board(board)

