#include <iostream>
#include <queue>
#include <vector>
#include <algorithm>

using namespace std;

const int dx[4] = {1, 0, -1, 0};
const int dy[4] = {0, 1, 0, -1};

struct State {
    vector<vector<int>> board;
    int steps;
};

bool operator<(const State& s1, const State& s2) {
    return s1.steps > s2.steps;
}

int main() {
    vector<vector<int>> board(3, vector<int>(3));
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            cin >> board[i][j];
        }
    }

    queue<State> q;
    q.push({board, 0});
    vector<vector<int>> target = {{1, 2, 3}, {4, 5, 6}, {7, 8, 0}};
    vector<vector<int>> visited(3, vector<int>(3, -1));

    while (!q.empty()) {
        State s = q.front();
        q.pop();

        if (s.board == target) {
            cout << s.steps << endl;
            return 0;
        }

        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                if (s.board[i][j] == 0) {
                    for (int k = 0; k < 4; k++) {
                        int ni = i + dx[k];
                        int nj = j + dy[k];

                        if (ni >= 0 && ni < 3 && nj >= 0 && nj < 3 && visited[ni][nj] == -1) {
                            visited[ni][nj] = 1;
                            vector<vector<int>> newBoard = s.board;
                            swap(newBoard[i][j], newBoard[ni][nj]);
                            q.push({newBoard, s.steps + 1});
                        }
                    }
                }
            }
        }
    }

    cout << -1 << endl;
    return 0;
}
