#include <stdio.h>
#include <string.h>

#define MAX_H 1000
#define MAX_W 1000
#define MAX_R 1000
#define MAX_C 1000

int main() {
    int H, W, R, C;
    char region[MAX_H][MAX_W + 1];
    char pattern[MAX_R][MAX_C + 1];

    scanf("%d %d", &H, &W);
    for (int i = 0; i < H; i++) {
        scanf("%s", region[i]);
    }

    scanf("%d %d", &R, &C);
    for (int i = 0; i < R; i++) {
        scanf("%s", pattern[i]);
    }

    for (int i = 0; i <= H - R; i++) {
        for (int j = 0; j <= W - C; j++) {
            int match = 1;
            for (int k = 0; k < R && match; k++) {
                for (int l = 0; l < C && match; l++) {
                    if (region[i + k][j + l] != pattern[k][l]) {
                        match = 0;
                    }
                }
            }
            if (match) {
                printf("%d %d\n", i, j);
            }
        }
    }

    return 0;
}
